from __future__ import division
from builtins import object
from past.utils import old_div
import os
from pathlib import Path

import numpy as np
from numpy.testing import assert_

ROOT = Path(np.__file__).parents[0]
FILES = [
    old_div(ROOT, "py.typed"),
    old_div(ROOT, "__init__.pyi"),
    old_div(ROOT, "ctypeslib.pyi"),
    old_div(old_div(ROOT, "core"), "__init__.pyi"),
    old_div(old_div(ROOT, "distutils"), "__init__.pyi"),
    old_div(old_div(ROOT, "f2py"), "__init__.pyi"),
    old_div(old_div(ROOT, "fft"), "__init__.pyi"),
    old_div(old_div(ROOT, "lib"), "__init__.pyi"),
    old_div(old_div(ROOT, "linalg"), "__init__.pyi"),
    old_div(old_div(ROOT, "ma"), "__init__.pyi"),
    old_div(old_div(ROOT, "matrixlib"), "__init__.pyi"),
    old_div(old_div(ROOT, "polynomial"), "__init__.pyi"),
    old_div(old_div(ROOT, "random"), "__init__.pyi"),
    old_div(old_div(ROOT, "testing"), "__init__.pyi"),
]


class TestIsFile(object):
    def test_isfile(self):
        """Test if all ``.pyi`` files are properly installed."""
        for file in FILES:
            assert_(os.path.isfile(file))
